import React, {useState} from 'react'; 
import Section from '../components/Section';

// import thingie from '../content/overview.txt';

class Home extends React.Component {
    render() {
        const [active, setActive] = useState('Overview');
        return (    
                <main className='main-home'>
                    <img src={require('../images/g_j-grad.jpg')} alt='Graham with his dog, Jenga'/>
                    <div className='summary-links' onClick={e => this.handleSummaryClick(e)}>
                        <p id='overview'>Overview</p>
                        <p id='bio'>Bio</p>
                        <p id='interests'>Interests</p>
                    </div>
                    <Section content={fetch('../content/overview.txt')} />
                </main>
        );
    }

    handleSummaryClick(e) {
        if (e.target.tagName === 'P') {
            const text = e.target.textContent;
            e.target.classList.toggle('selected');



            console.log('you clicked on one of these summary links!', text);
        }
    }

}

export default Home
