import React from 'react';
import DOMPurify from 'dompurify';

class Section extends React.Component {
    render() {
        //content is the name of a text file to be read and rendered

        let innerHTML = this.props.content;
        let images = this.props.images;
        if (images) {
            for (let info in images) {
                let img = <img src={info[0]} alt={info[1]}/>
                innerHTML = img + innerHTML
            }
        }
        return (
            <div className='section' 
                    dangerouslySetInnerHTML={{__html: DOMPurify.sanitize(innerHTML)}}
                >
            </div>
        )
    }
}

export default Section;
