import React from 'react'; 
import Section from '../components/Section';

class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          text: 'Overview'
        };
      }

    render() {
        return (    
                <main className='main-home'>
                    <img src={require('../images/g_j-grad.jpg')} alt='Graham with his dog, Jenga'/>
                    <div className='summary-links' onClick={e => this.handleSummaryClick(e)}>
                        <p id='overview'>Overview</p>
                        <p id='bio'>Bio</p>
                        <p id='interests'>Interests</p>
                    </div>
                    {this.state.text === 'Overview' && <Section content='bullshit1' />}
                    {this.state.text === 'Bio' && <Section content='bullshit2' />}
                    {this.state.text === 'Interests' && <Section content='bullshit3' />}
                </main>
        );
    }

    handleSummaryClick(e) {
        if (e.target.tagName === 'P') {
            console.log(e.target.parentNode);




            e.target.classList.toggle('selected');
            this.setState({text: e.target.textContent});


            console.log(this.state.text);
        }
    }

}

export default Home
